let tooltipItems=document.querySelectorAll('[data-bs-toggle="tooltip"]');const elementsToCopy=document.querySelectorAll("code");let tooltipTriggerList=[].slice.call(tooltipItems);let tooltipList=tooltipTriggerList.map(function(tooltipTriggerEl){return new bootstrap.Tooltip(tooltipTriggerEl)})
tooltipItems.forEach((tooltipItem,index)=>{tooltipItem.addEventListener("click",(e)=>{const textToCopy=elementsToCopy[index].textContent;const textarea=document.createElement("textarea");textarea.value=textToCopy;document.body.appendChild(textarea);textarea.select();document.execCommand("copy");document.body.removeChild(textarea);let tooltip=e.target;const tooltipInstance=bootstrap.Tooltip.getInstance(tooltip);tooltipInstance.setContent({'.tooltip-inner':'Copied!'});setTimeout(()=>{tooltipInstance.setContent({'.tooltip-inner':'Copy'})},1500)})})